import { createSlice } from '@reduxjs/toolkit'

export const dataUser = createSlice({

    name: 'blogs',
    initialState: {
        token_keys: {} as object,
        user: {} as object,
        display: true as boolean,
        content: [],

        page: {
            currentPage: 1 as number,
            skip: 0 as number,
            take: 4 as number
        } as any,

        edit_post: {
            infouser: { username: '' },
            updatedAt: '',
            title: '',
            content: ''
        } as object,

        index_post: [] as object[],
        myPost: [[], 0] as any[]
    },

    reducers: {

        setContent: (state, action) => {
            const { payload } = action
            state.content = payload
        },

        firstPage: state => {
            state.page.currentPage = 1
            state.page.skip = (state.page.currentPage * state.page.take) - state.page.take
        },

        targetPage: (state, action) => {
            const { payload } = action
            state.page.currentPage = payload
            state.page.skip = (state.page.currentPage * state.page.take) - state.page.take
        },

        nextPage: state => {
            state.page.currentPage += 1
            state.page.skip = (state.page.currentPage * state.page.take) - state.page.take
        },

        previousPage: state => {
            state.page.currentPage -= 1
            state.page.skip = (state.page.currentPage * state.page.take) - state.page.take
        },

        setDisplay: (state, action) => {
            const { payload } = action
            state.display = payload
        },

        setIndexPost: (state, action) => {
            const { payload } = action
            state.index_post = payload
        },

        setMyPost: (state, action) => {
            const { payload } = action
            state.myPost = payload
        },

        setEditPost: (state, action) => {
            const { payload } = action
            state.edit_post = payload
        },

        setDataUser: (state, action) => {
            const { payload } = action
            state.user = payload
        },

        setTokenKeys: (state, action) => {
            const { payload } = action
            state.token_keys = payload
        },

        reset: state => {
            state.user = {}
            state.token_keys = {}
            state.myPost = [[], 0]
        }
    }
})

export const {
    setDataUser, reset, setIndexPost,
    setMyPost, setTokenKeys, setEditPost,
    setDisplay, nextPage, previousPage,
    firstPage, targetPage
} = dataUser.actions

export default dataUser.reducer
