import { FC } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { setEditPost, setDisplay, setContent, setString } from '../store/data'
import { Container, Row, Col, Card, Button } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import Editpost from './Editpost'
import Pagination from './Pagination'
import Api from '../api/Api'


const Myposts: FC = () => {

    const { myPost, user, display } = useSelector((state: any) => state.data)
    const { page, content, str } = useSelector((state: any) => state.page)
    const key = Object.keys(user)
    const history = useHistory()
    const dispatch = useDispatch()

    // const { index_post } = useSelector((state: any) => state.data)
    const countPosts = myPost[1]
    const count = Math.ceil(countPosts / page.take)

    if (key.length === 0) {
        history.push('/')
    }

    const btnEdit = (result: any) => {
        console.log(result)
        dispatch(setEditPost(result))
        dispatch(setDisplay(false))
    }

    const btnDelete = async (uuid: any) => {
        const api = new Api({})
        const data: any = await api.deletePost({ uuid })
        if (data) {
            window.location.reload()
        }
    }


    // less-more
    const long_text: string[] = []
    let getNumber = 0

    const contentData = (text: any) => { // --> ini

        const words = text.match(/(\w+)/g).length
        if (words >= 100) {
            dispatch(setString(text))
            long_text.push(str)
        }
        return words <= 100 ? text : slicer(text)
    }

    const fullContentReader: any = (text: any) => { // --> ini
        const words = text.match(/(\w+)/g).length
        getNumber++
        return words > 100 ? <Button variant="dark" className="thisButton" id={`${getNumber}`} onClick={displayfull}>Read More</Button> : null
    }

    const slicer = (text: string) => {
        const slicer = text.slice(0, 200)
        return `${slicer}...`
    }

    const displayfull = (e: any) => {
        let { textContent, id } = e.target
        const data_id: any = document.getElementById(id)
        const { parentNode } = data_id
        const parentDiv = parentNode.childNodes[4]
        const index = parseInt(id) - 1


        console.log(str)
        dispatch(setContent(long_text))


        if (textContent === 'Read More') {
            parentDiv.innerHTML = content[index]
            data_id.innerHTML = 'Show less'
        } else {
            parentDiv.innerHTML = slicer(content[index])
            data_id.innerHTML = 'Read More'
        }
    }


    switch (myPost[1]) {
        case 0:
            return (
                <div className="App">
                    <Container>
                        <Row>
                            <Col md>
                                <div className='App blogs'>
                                    <Card>
                                        <Card.Body>
                                            <Card.Title>
                                                No posts yet
                                            </Card.Title>
                                            <hr />
                                            <Card.Text>
                                                Let's add your first post!
                                            </Card.Text>
                                            <Button variant="dark" onClick={() => history.push('/home')}>Add</Button>
                                        </Card.Body>
                                    </Card>
                                    <br />
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </ div>
            )

        default:
            if (display === true) {
                return (
                    <>
                        {
                            myPost[0].map((result: any) =>
                                <div className='App blogs' key={result.uuid} >
                                    <Card>
                                        <Card.Body>
                                            <Card.Text>
                                                By : {result.infouser.username}
                                            </Card.Text>
                                            <Card.Text>
                                                Last update : {result.updatedAt}
                                            </Card.Text>
                                            <Card.Title>
                                                {result.title}
                                            </Card.Title>
                                            <hr />
                                            <Card.Text>
                                                {contentData(result.content)}
                                            </Card.Text>
                                            <Button variant="dark" onClick={() => btnEdit(result)}>Edit</Button> {' '}
                                            <Button variant="dark" onClick={() => btnDelete(result.uuid)}>Delete</Button> {' '}
                                            {fullContentReader(result.content)}
                                        </Card.Body>
                                    </Card>
                                    <br />
                                </div>)
                        }
                        < Pagination count={count} />
                    </>
                )
            } else {
                return <Editpost />
            }
    }
}


export default Myposts
