import { FC, useState } from 'react'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { Card, Form, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'
import Api from '../api/Api'
import { setDataUser, setTokenKeys } from '../store/data'


const Login: FC = () => {

    const [getValue, setValue] = useState({})
    const dispatch = useDispatch()
    const history = useHistory()

    const getText = (e: any) => {
        const { name }: any = e.target
        const { value }: any = e.target
        setValue({ ...getValue, [name]: value })
    }

    const submit = async () => {
        const api = new Api(getValue)
        const res: any = await api.login()
        // console.log(res)
        if (res) {
            const { token } = res
            dispatch(setTokenKeys(res))
            const userData = await api.tokenValidation(token)
            if (userData) {
                dispatch(setDataUser(userData))
                history.push('/home')
            }
        }
    }

    return (
        <>
            <Navigation />
            <div className="App">
                <Card>
                    <Form>
                        <Form.Group controlId="formEmail">
                            <Form.Label>Email</Form.Label>
                            <Form.Control type="email" name="email" onChange={e => getText(e)} placeholder="example@email.com" />
                        </Form.Group>
                        <Form.Group controlId="formPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" name="password" onChange={e => getText(e)} placeholder="Your password" />
                        </Form.Group>
                        <br />
                        <Button variant="dark" onClick={submit}>Login</Button>
                    </Form>
                </Card>
            </div>
        </>
    )
}

export default Login
