import { Component } from "react"
import { Pagination } from 'react-bootstrap'
import Index from '../pages/Index'


interface Props {
    count: number
}

interface State {
    currentPage: number
}


class Pagin extends Component<Props, State> {


    constructor(props: any) {
        super(props)
        this.state = {
            currentPage: 1
        }
        new Index(this.state.currentPage)
    }


    btnNumber = () => {
        const pageNumber = []
        for (let i = 1; i <= this.props.count; i++) {
            pageNumber.push(<Pagination.Item key={i} active={i === this.state.currentPage} onClick={() => this.targetButton(i)} >{i}</Pagination.Item>)
        }
        return pageNumber
    }

    targetButton = (num: number) => {
        this.setState({ currentPage: num })
        new Index(num)
    }

    firstButton = () => {
        this.setState({ currentPage: 1 })
    }

    nextButton = () => {
        this.setState({ currentPage: this.state.currentPage + 1 })
        const page = new Index(this.state.currentPage + 1)
        page.updatePage()
    }

    previousButton = () => {
        this.setState({ currentPage: this.state.currentPage - 1 })
        new Index(this.state.currentPage + 1)
    }

    lastButton = () => {
        this.targetButton(this.props.count)
        new Index(this.props.count)
    }

    render = () => {
        return (
            <div className="pagination">
                <Pagination size="sm">
                    <Pagination.First disabled={this.state.currentPage === 1} onClick={this.firstButton} />
                    <Pagination.Prev disabled={this.state.currentPage === 1} onClick={this.previousButton} />
                    {
                        this.btnNumber()
                    }
                    <Pagination.Next disabled={this.state.currentPage === this.props.count} onClick={this.nextButton} />
                    <Pagination.Last disabled={this.state.currentPage === this.props.count} onClick={this.lastButton} />
                </Pagination>
            </div>
        )
    }
}


export default Pagin
