import { Component } from 'react'
import '../css/App.css';
import Index from './Index'
import Login from './Login'
import Signup from './Signup'

class App extends Component {

  path: string = window.location.pathname

  router = (): JSX.Element => {
    switch (this.path) {
      case '/signup':
        return <Signup />

      case '/login':
        return <Login />

      default:
        return <Index />
    }
  }

  render = (): JSX.Element => {
    return this.router()
  }

}

export default App;
