import { Component } from "react"
import { Button, Form } from 'react-bootstrap'
import { connect } from 'react-redux'
import Api from '../api/Api'


interface Props {
    data: {
        login: boolean
        user: {
            uuid: string
        }
        index_post: {
            0: any
        }
    }
    info: any
}

interface State {
    comments: string
}


class Comment extends Component<Props, State> {

    constructor(props: any) {
        super(props)
        this.state = {
            comments: ''
        }
    }

    commentValue = (e: any) => {
        const { value } = e.target
        this.setState({ comments: value })
    }

    submit = async (): Promise<void> => {
        if (this.props.data.login === false) {
            alert('Login first!')
        } else {
            // console.log(this.state)
            const { comments } = this.state
            const { uuid } = this.props.info
            const data = {
                user_uuid: this.props.data.user.uuid,
                post_uuid: uuid,
                comments
            }
            // console.log(data)
            try {
                const api = new Api(data)
                const result: any = await api.addComment()
                if (result) {
                    console.log(result)
                }
            } catch (error) {
                const { data } = error.response
                alert(data.message)
            }

        }
    }

    render = () => {

        console.log(this.props.data.index_post[0])

        return (
            <>
                <br /> <hr />
                <Form.Control as="textarea" placeholder="Add comments" name="comments" onChange={this.commentValue} rows={2} />
                <br />
                <Button variant="dark" onClick={this.submit}>Comment</Button>
            </>
        )
    }
}


const mapStateToProps = (state: any) => ({
    data: state.data
})


export default connect(mapStateToProps)(Comment)
