import { FC } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { setPage } from '../store/data'
import { Pagination } from 'react-bootstrap'


const Pagin: FC = () => {

    const { index_post } = useSelector((state: any) => state.data)
    const { page } = useSelector((state: any) => state.page)
    const dispatch = useDispatch()
    const countPosts = index_post[1]
    const lengthOfPage = Math.ceil(countPosts / page.take)

    const btnNumber = () => {
        const pageNumber = []
        for (let i = 1; i <= lengthOfPage; i++) {
            pageNumber.push(<Pagination.Item active={i === page.currentPage}>{i}</Pagination.Item>)
        }
        return pageNumber
    }

    const firstPage = () => {
        console.log(countPosts)
    }

    const nextPage = () => {
        dispatch(setPage())
    }

    return (
        <div className="pagination">
            <Pagination size="sm">
                <Pagination.First onClick={firstPage} />
                <Pagination.Prev disabled={page.first} />
                {
                    btnNumber()
                }
                <Pagination.Next onClick={nextPage} />
                <Pagination.Last />
            </Pagination>
        </div>
    )
}

export default Pagin
