import { FC, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { setIndexPost } from '../store/data'
import { Container, Row, Col, Card, Alert } from 'react-bootstrap'
import Navigation from '../components/Navigation'
import Pagination from '../components/Pagination'
import Api from '../api/Api'

const Index: FC = () => {

    const dispatch = useDispatch()
    const [loading, setLoading] = useState(false)


    useEffect(() => {
        const fetch = async (): Promise<void> => {
            setLoading(true)
            const api = new Api({})
            const data = await api.index()
            dispatch(setIndexPost(data))
        }
        fetch()
    }, [dispatch, setLoading])

    const { index_post }: any = useSelector((state: any) => state.data)
    // console.log(index_post)

    return (
        <>
            <Navigation />
            <div className="App">
                <Container>
                    <Row>
                        <Col md>
                            {loading ?
                                index_post[0].map((result: any) =>
                                    <div className='App blogs' key={result.uuid}>
                                        <Card>
                                            <Card.Body>
                                                <Card.Text>
                                                    By : {result.infouser.username}
                                                </Card.Text>
                                                <Card.Text>
                                                    Last update : {result.updatedAt}
                                                </Card.Text>
                                                <Card.Title>
                                                    {result.title}
                                                </Card.Title>
                                                <hr />
                                                <Card.Text>
                                                    {result.content}
                                                </Card.Text>
                                            </Card.Body>
                                        </Card>
                                        <br />
                                    </div>
                                ) : <Alert variant="success">Data sedang diproses... </Alert>
                            }
                        </Col>
                    </Row>
                </Container>
                <Pagination />
            </ div>
        </>
    )
}

export default Index
