import { Component } from "react"
import { Button } from 'react-bootstrap'


interface Props {
    text: string
}

interface State {
    textBody: string
    id: number
}

class HiddenButton extends Component<Props, State> {

    id: number = 0

    constructor(props: any) {
        super(props)
        this.state = {
            textBody: this.props.text,
            id: 1
        }
    }

    componentDidMount = () => {
        this.setState({ id: this.state.id + 1 })
    }

    readMore = (e: any) => {
        // console.log(this.state.textBody)
        console.log(e.target)
    }

    isDisplay = () => {
        const { text }: any = this.props
        const words = text.match(/(\w+)/g).length
        return words >= 100 ? <Button onClick={this.readMore} id={`${this.state.id}`} variant="dark" >Read more</Button> : <p></p>
    }

    textBody = (text: any) => {

        const words = text.match(/(\w+)/g).length
        if (words >= 100) {
            console.log(words)
        }
        return words <= 100 ? text : this.slicer(text)
    }

    slicer = (text: string) => {
        const slicer = text.slice(0, 200)
        return `${slicer}...`
    }

    render = () => {
        return this.isDisplay()
    }
}

export default HiddenButton
