import { Component } from "react"
import { Card, Form, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'

interface State {
    user: any
}

class Login extends Component<any, State> {


    constructor(props: any) {
        super(props)
        this.state = {
            user: {
                email: '' as string,
                password: '' as string
            }
        }
    }

    getChange = (e: any): void => {
        const { name, value } = e.target
        this.setState({
            user: { ...this.state.user, [name]: value }
        })
    }

    submit = (): void => {
        console.log(this.state.user)
    }

    render = (): JSX.Element => {
        return (
            <>
                <Navigation />
                <div className="App">
                    <Card>
                        <Form>
                            <h3>Login</h3>
                            <br />
                            <Form.Group>
                                <Form.Control type="email" name="email" onChange={this.getChange} placeholder="Username" />
                            </Form.Group>
                            <br />
                            <Form.Group>
                                <Form.Control type="password" name="password" onChange={this.getChange} placeholder="Password" />
                            </Form.Group>
                            <br />
                            <Button onClick={this.submit}>Login</Button>
                        </Form>
                    </Card>
                </div>
            </>
        )
    }

}

export default Login
