import { FC, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { setIndexPost } from '../store/data'
import { Container, Row, Col, Card, Alert, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'
import Pagination from '../components/Pagination'
import Api from '../api/Api'


const Index: FC = () => {

    const dispatch = useDispatch()
    const [loading, setLoading] = useState(false)
    const { index_post }: any = useSelector((state: any) => state.data)
    const { page } = useSelector((state: any) => state.page)
    const { skip, take } = page

    const countPosts = index_post[1]
    const count = Math.ceil(countPosts / page.take)

    useEffect(() => {
        const fetch = async (): Promise<void> => {
            setLoading(true)
            const api = new Api({})
            const data = await api.index({ skip, take })
            dispatch(setIndexPost(data))
        }
        fetch()
    }, [dispatch, setLoading, skip, take])


    // console.log(index_post)

    // const content = document.getElementsByClassName('content')
    // const contentText: any = []

    // for (let i = 0; i < content.length; i++) {
    //     contentText.push(content[i].innerHTML)
    //     let words = contentText[i].match(/(\w+)/g).length
    //     console.log(words)
    // }

    const content = (text: any) => {
        const chars = text.length
        const words = text.match(/(\w+)/g).length
        console.log(`Chars : ${chars} | Words : ${words}`)

        if (words <= 100) {
            return text
        } else {
            return `Sedang diperbaiki`
        }
    }

    return (
        <>
            <Navigation />
            <div className="App">
                <Container>
                    <Row>
                        <Col md>
                            {loading ?
                                index_post[0].map((result: any) =>
                                    <div className='App blogs' key={result.uuid}>
                                        <Card>
                                            <Card.Body>
                                                <Card.Text>
                                                    By : {result.infouser.username}
                                                </Card.Text>
                                                <Card.Text>
                                                    Last update : {result.updatedAt}
                                                </Card.Text>
                                                <Card.Title>
                                                    {result.title}
                                                </Card.Title>
                                                <hr />
                                                <Card.Text className="content">
                                                    {content(result.content)}
                                                </Card.Text>
                                            </Card.Body>
                                            <Button variant="dark">Read More</Button>
                                        </Card>
                                        <br />
                                    </div>
                                ) : <Alert variant="success">Data sedang diproses... </Alert>
                            }
                        </Col>
                    </Row>
                </Container>
            </ div>
            <Pagination count={count} />
        </>
    )
}

export default Index
