import { FC, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Card, Button, Form } from 'react-bootstrap'
import { setDisplay } from '../store/data'
import Api from '../api/Api'

const Editpost: FC = () => {

    const dispatch = useDispatch()
    const { edit_post } = useSelector((state: any) => state.data)
    const { uuid, title, content } = edit_post
    const [getData, setData] = useState({ uuid, title, content })

    const changeValue = (e: any) => {
        const { name } = e.target
        const { value } = e.target
        setData({ ...getData, [name]: value })
    }

    const backTo = () => {
        dispatch(setDisplay(true))
    }

    const update = async () => {
        const api = new Api(getData)
        await api.editPost()
        dispatch(setDisplay(true))
        window.location.reload()
    }

    return (
        <div className='App blogs' >
            <Card>
                <Card.Body>
                    <Card.Text>
                        By : {edit_post.infouser.username}
                    </Card.Text>
                    <Card.Text>
                        Last update : {edit_post.updatedAt}
                    </Card.Text>
                    <Form.Label>Title</Form.Label>
                    <Form.Control as="textarea" name="title" onChange={changeValue} defaultValue={title} rows={1} />
                    <Form.Label>Content</Form.Label>
                    <Form.Control as="textarea" name="content" onChange={changeValue} defaultValue={content} rows={5} />
                    <br />
                    <Button variant="dark" onClick={update} >Update</Button>{' '}
                    <Button variant="dark" onClick={backTo} >Cencle</Button>
                </Card.Body>
            </Card>
            <br />
        </div>
    )
}

export default Editpost
