import { Component } from 'react'
import { connect } from 'react-redux'
import { Card, Form, Button, Container } from 'react-bootstrap'


interface Props {
    page: {
        edit_post: {
            uuid: string
            username: string
            updatedAt: string
            title: string
            content: string
        }
    }
}


class EditBlogs extends Component<Props> {

    dataValue = () => {

    }

    btnEdit = () => {
        console.log(this.props.page.edit_post)
    }

    render = () => {

        const { username, updatedAt } = this.props.page.edit_post

        return (
            <Container>
                <Card>
                    <Card.Body>

                        <Form.Label>By: {username}</Form.Label>
                        <br />
                        <Form.Label>Last update: {updatedAt}</Form.Label>
                        <br />
                        <Form.Label>Title</Form.Label>
                        <Form.Control as="textarea" onChange={this.dataValue} name="title" rows={1} />
                        <br />
                        <Form.Label>Content</Form.Label>
                        <Form.Control as="textarea" onChange={this.dataValue} name="content" rows={4} />
                        <br />
                        <Button variant="dark" onClick={this.btnEdit} >Edit</Button>
                    </Card.Body>
                </Card>
            </Container>
        )
    }
}

const mapStateToProps = (state: any) => ({
    page: state.page
})

export default connect(mapStateToProps)(EditBlogs)
