import { Component } from 'react'
import Navigation from '../components/Navigation'
import { Container, Row, Col, Card, Alert, Button } from 'react-bootstrap'
import HiddenButton from '../components/HiddenButton'
import Api from '../api/Api'
import Pagination from '../components/Pagin'

interface State {
    params: {
        currentPage: any
        take: number
    }
    data: any
    loading: boolean
    countData: number
}

class Index extends Component<any, State> {

    hidden: HiddenButton = new HiddenButton({})
    data: any

    constructor(props: any) {
        super(props)
        const str = typeof props
        const data = str === 'object' ? 1 : props
        this.data = data
        this.state = {
            params: {
                currentPage: data,
                take: 4
            },
            loading: false,
            countData: 0,
            data: [{}, 0]
        }
        console.log(`Constructor --> data : ${data}`)
        console.log(this.state.params)
    }


    // life cycle

    static getDerivedStateFromProps = (props: any, state: State) => {
        // console.log(`getDerived`)
        return null
    }

    componentDidMount = () => {
        console.log(`Mounted`)
        this.nextPage()
    }

    shouldComponentUpdate = (): boolean => {
        console.log(`Should be updated? --> ${this.data}`)
        return true
    }

    componentDidUpdate = (): void => {
        console.log(`Updated`)
    }

    componentWillUnmount = () => {
        console.log(`Will unmount`)
    }

    updatePage = async (): Promise<void> => {
        console.log(`Update page ${this.state.params}`)
        const api = new Api(this.state.params)
        const data = await api.index()
        this.setState({ data, loading: true })
        const count = Math.ceil(this.state.data[1] / this.state.params.take)
        this.setState({ countData: count })
    }

    nextPage = async () => {
        this.setState({
            params: {
                currentPage: this.state.params.currentPage + 1,
                take: 4
            }
        })
        const api = new Api(this.state.params)
        const data = await api.index()
        this.setState({ data, loading: true })

        const count = Math.ceil(this.state.data[1] / this.state.params.take)
        this.setState({ countData: count })
    }

    render = (): JSX.Element => {

        // console.log(`Render`)
        // console.log(this.state.data[0])

        return (
            <>
                <Navigation />
                <div className="App">
                    <Container>
                        <Row>
                            <Col md>
                                {this.state.loading ?
                                    this.state.data[0].map((result: any) =>
                                        <div className='App blogs' key={result.uuid}>
                                            <Card>
                                                <Card.Body>
                                                    <Card.Text>
                                                        By : {result.infouser.username}
                                                    </Card.Text>
                                                    <Card.Text>
                                                        Last update : {result.updatedAt}
                                                    </Card.Text>
                                                    <Card.Title>
                                                        {result.title}
                                                    </Card.Title>
                                                    <hr />
                                                    <Card.Text className="content">
                                                        {this.hidden.textBody(result.content)}
                                                    </Card.Text>
                                                </Card.Body>
                                                <Button onClick={this.nextPage}>Current page: {this.state.params.currentPage - 1}</Button>
                                                <br />
                                                <HiddenButton text={result.content} />
                                            </Card>
                                            <br />
                                        </div>
                                    ) : <Alert variant="success">Processing data... </Alert>
                                }
                            </Col>
                        </Row>
                    </Container>
                    <Pagination count={this.state.countData} nextPage={this.nextPage} />
                </div>
            </>
        )
    }
}

export default Index
