import { FC, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { setIndexPost, setContent, setDisplay, setButtonNumber } from '../store/data'
import { Container, Row, Col, Card, Alert, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'
import Pagination from '../components/Pagination'
import Api from '../api/Api'


const Index: FC = () => {

    const dispatch = useDispatch()
    const [loading, setLoading] = useState<boolean>(false)
    const { index_post }: any = useSelector((state: any) => state.data)
    const { page, display, button_number } = useSelector((state: any) => state.page)
    const { skip, take } = page

    const countPosts = index_post[1]
    const count = Math.ceil(countPosts / page.take)

    useEffect(() => {
        const fetch = async (): Promise<void> => {
            setLoading(true)
            const api = new Api({})
            const data = await api.index({ skip, take })
            dispatch(setIndexPost(data))
        }
        fetch()
    }, [dispatch, setLoading, skip, take])


    const content_text: any = []

    const content = (text: any) => {
        const slicer = text.slice(0, 200)
        const words = text.match(/(\w+)/g).length

        if (words >= 100) {
            content_text.push(text)
        }

        return words <= 100 ? text : `${slicer}...`
    }


    const displayRead: any = (text: any) => {
        const words = text.match(/(\w+)/g).length

        dispatch(setButtonNumber())

        function displayfull(e: any) {
            const hide: any = document.getElementsByClassName('hide')
            const allHide = hide.length
            dispatch(setContent(content_text))
            dispatch(setDisplay(!display))

            let { textContent } = e.target
            console.log(textContent)

            console.log(display)
        }

        return words > 100 ? <Button variant="dark" className="hide" id={`numberButton${button_number}`} onClick={displayfull}>Read More</Button> : null
    }

    return (
        <>
            <Navigation />
            <div className="App">
                <Container>
                    <Row>
                        <Col md>
                            {loading ?
                                index_post[0].map((result: any) =>
                                    <div className='App blogs' key={result.uuid}>
                                        <Card>
                                            <Card.Body>
                                                <Card.Text>
                                                    By : {result.infouser.username}
                                                </Card.Text>
                                                <Card.Text>
                                                    Last update : {result.updatedAt}
                                                </Card.Text>
                                                <Card.Title>
                                                    {result.title}
                                                </Card.Title>
                                                <hr />
                                                <Card.Text className="content">
                                                    {content(result.content)}
                                                </Card.Text>
                                            </Card.Body>
                                            {displayRead(result.content)}
                                        </Card>
                                        <br />
                                    </div>
                                ) : <Alert variant="success">Data sedang diproses... </Alert>
                            }
                        </Col>
                    </Row>
                </Container>
            </ div>
            <Pagination count={count} />
        </>
    )
}

export default Index
