import { Component } from "react"
import { connect } from 'react-redux'
// import { Card } from 'react-bootstrap'


interface Props {
    data: {
        index_post: {
            0: any
            1: number
        }
    }
}


class DisplayComments extends Component<Props> {


    render = () => {

        const { index_post } = this.props.data
        console.log(index_post[0])

        return (
            <>
                {
                    index_post[0].map((res: any) =>
                        <div key={res.uuid}>
                            {
                                res.infocomments[0].length !== 0 ?
                                    <>
                                        <p>{res.infocomments[0].username} :</p>
                                        <p>{res.infocomments[0].comments}</p>
                                    </> :
                                    null
                            }
                            <hr />
                        </ div>
                    )
                }
            </>
        )
    }

}


const mapStateToProps = (state: any) => ({
    data: state.data
})


export default connect(mapStateToProps)(DisplayComments)
