import { FC } from 'react'
import { Navbar, Nav, Container } from 'react-bootstrap'

const Navigation: FC = () => {

    const path: string = window.location.pathname


    const isHome = (path: string) => {
        switch (path) {
            case '/home':
                const pathHome: string = window.location.pathname
                return (
                    <>
                        <Nav.Link href="/home/myblogs" active={pathHome === '/home/myblogs'} >My blogs</Nav.Link>
                        <Nav.Link href="/home/info" active={pathHome === '/home/info'} >Info</Nav.Link>
                    </>
                )
            default:
                return (
                    <>
                        <Nav.Link href="/login" active={path === '/login'} >Login</Nav.Link>
                        <Nav.Link href="/signup" active={path === '/signup'} >Signup</Nav.Link>
                    </>
                )

        }
    }

    return (
        <div className="navigation">
            <Navbar bg="dark" variant="dark">
                <Container>
                    <Navbar.Brand href="/">SimpleBlog</Navbar.Brand>
                    <Nav className="me-auto">
                        {isHome(path)}
                    </Nav>
                </Container>
            </Navbar>
        </ div>
    )

}

export default Navigation
