import { Component } from 'react'
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap'
import { useSelector } from 'react-redux'
import Logout from './Logout'

class Navigation extends Component {

    path: string = window.location.pathname
    user: any

    constructor(props: any) {
        super(props)
        const { user } = useSelector((state: any) => state.data)
        this.user = user
    }

    isHome = (path: string): JSX.Element => {

        const currentNav = () => {
            return (
                <>
                    <Nav.Link href="/home" active={path === '/home'} >Home</Nav.Link>
                    <Nav.Link href="/home/myblogs" active={path === '/home/myblogs'} >My blogs</Nav.Link>
                    <Nav.Link href="/home/info" active={path === '/home/info'} >Info</Nav.Link>

                    {/* <Navbar.Toggle aria-controls="responsive-navbar-nav" /> */}
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav>
                            <Nav className="me-auto">
                                <NavDropdown title="User" id="collasible-nav-dropdown">
                                    <NavDropdown.Item >{this.user.username}</NavDropdown.Item>
                                    <NavDropdown.Divider />
                                    <NavDropdown.Item >
                                        <Logout />
                                    </NavDropdown.Item>
                                </NavDropdown>
                            </Nav>
                        </Nav>
                    </Navbar.Collapse>
                </>
            )
        }

        switch (path) {
            case '/home':
            case '/home/info':
            case '/home/myblogs':
                return currentNav()

            case "/":
                const key = Object.keys(this.user)
                if (key.length === 0) {
                    return (
                        <>
                            <Nav.Link href="/login" >Login</Nav.Link>
                            <Nav.Link href="/signup" >Signup</Nav.Link>
                        </>
                    )
                } else return currentNav()

            default:
                return (
                    <>
                        <Nav.Link href="/login" active={path === '/login'} >Login</Nav.Link>
                        <Nav.Link href="/signup" active={path === '/signup'} >Signup</Nav.Link>
                    </>
                )

        }
    }

    render = (): JSX.Element => {
        return (
            <div className="navigation">
                <Navbar collapseOnSelect bg="dark" variant="dark">
                    <Container>
                        <Navbar.Brand href="/">SimpleBlog</Navbar.Brand>
                        <Nav className="me-auto">
                            {this.isHome(this.path)}
                        </Nav>
                    </Container>
                </Navbar>
            </ div>
        )
    }

}

export default Navigation
