import { FC, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { setIndexPost, setContent, setDisplay } from '../store/data'
import { Container, Row, Col, Card, Alert, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'
import Pagination from '../components/Pagination'
import Api from '../api/Api'


const Index: FC = () => {

    const dispatch = useDispatch()
    const [loading, setLoading] = useState<boolean>(false)
    const { index_post }: any = useSelector((state: any) => state.data)
    const { page, display } = useSelector((state: any) => state.page)
    const { skip, take } = page

    const countPosts = index_post[1]
    const count = Math.ceil(countPosts / page.take)

    useEffect(() => {
        const fetch = async (): Promise<void> => {
            setLoading(true)
            const api = new Api({})
            const data = await api.index({ skip, take })
            dispatch(setIndexPost(data))
        }
        fetch()
    }, [dispatch, setLoading, skip, take])


    // Read More button

    const content_text: any = []
    let getNumber = 0

    const content = (text: any) => {
        const slicer = text.slice(0, 200)
        const words = text.match(/(\w+)/g).length

        if (words >= 100) {
            content_text.push(text)
        }

        // return words <= 100 ? text : `${slicer}...`

        if (words <= 100) {
            return text
        } else {
            if (display === false) {
                return text
            } else {
                return `${slicer}...`
            }
        }
    }

    const displayfull = (e: any) => {
        let { textContent, id } = e.target
        const data_id: any = document.getElementById(id)
        const { parentNode } = data_id
        const parentDiv = parentNode.childNodes[0]
        const text_content = parentDiv.childNodes[4]

        dispatch(setContent(content_text))
        dispatch(setDisplay(!display))

        console.log(text_content.innerHTML)

        if (textContent === 'Read More') {
            data_id.innerHTML = 'Show less'
        } else {
            data_id.innerHTML = 'Read More'
        }
    }

    const displayRead: any = (text: any) => {
        const words = text.match(/(\w+)/g).length
        getNumber++
        return words > 100 ? <Button variant="dark" className="thisButton" id={`${getNumber}`} onClick={displayfull}>Read More</Button> : null
    }

    return (
        <>
            <Navigation />
            <div className="App">
                <Container>
                    <Row>
                        <Col md>
                            {loading ?
                                index_post[0].map((result: any) =>
                                    <div className='App blogs' key={result.uuid}>
                                        <Card>
                                            <Card.Body>
                                                <Card.Text>
                                                    By : {result.infouser.username}
                                                </Card.Text>
                                                <Card.Text>
                                                    Last update : {result.updatedAt}
                                                </Card.Text>
                                                <Card.Title>
                                                    {result.title}
                                                </Card.Title>
                                                <hr />
                                                <Card.Text className="content">
                                                    {content(result.content)}
                                                </Card.Text>
                                            </Card.Body>
                                            {displayRead(result.content)}
                                        </Card>
                                        <br />
                                    </div>
                                ) : <Alert variant="success">Data sedang diproses... </Alert>
                            }
                        </Col>
                    </Row>
                </Container>
            </ div>
            <Pagination count={count} />
        </>
    )
}

export default Index
