import axios from 'axios'

export default class Api {

    api: any
    req: any

    constructor(req: any) {
        this.req = req
        this.api = axios.create({
            baseURL: `http://localhost:5000`,
            headers: {
                "Content-type": "application/json"
            }
        })
    }

    login = async (): Promise<void> => {
        const res: any = await this.api.post('/login', this.req)
        if (res) {
            const { data } = res
            return data
        } else {
            return res
        }
    }

    tokenValidation = async (token: any) => {

        const user: any = await this.api.get('/user', { headers: { "Authorization": `Bearer ${token}` } })
        if (user) {
            const { data } = user
            return data
        } else {
            return user
        }
    }


    register = async (): Promise<void> => {
        const res: any = await this.api.post('/signup', this.req)
        if (res) {
            const { data } = res
            return data
        } else {
            return res
        }
    }

    myBlogs = async (uuid: any): Promise<void> => {
        const res: any = await this.api.get('/myblogs', { params: { uuid } })
        if (res) {
            const { data } = res
            return data
        }
    }

    index = async (): Promise<void> => {
        const res: any = await this.api.get('/index')
        if (res) {
            const { data } = res
            return data
        }
    }
}
