import { Component } from "react"
import { Button } from 'react-bootstrap'


const slicer = (text: string) => {
    const slicer = text.slice(0, 200)
    return `${slicer}...`
}

export const textBody = (text: any) => {

    const words = text.match(/(\w+)/g).length
    if (words >= 100) {
        // console.log(words)
    }
    return words <= 100 ? text : slicer(text)
}


interface Props {
    text: string
}

class HiddenButton extends Component<Props> {

    text_body = textBody(this.props.text)

