import { FC } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { nextPage, previousPage, firstPage, targetPage } from '../store/data'
import { Pagination } from 'react-bootstrap'
import { count } from 'console'


interface counterPage {
    count: number
}


const Pagin: FC<counterPage> = ({ count }) => {

    const dispatch = useDispatch()
    const { page } = useSelector((state: any) => state.page)
    // const { index_post } = useSelector((state: any) => state.data)
    // const countPosts = index_post[1]
    // const lengthOfPage = Math.ceil(countPosts / page.take)

    const btnNumber = () => {
        const pageNumber = []
        for (let i = 1; i <= count; i++) {
            pageNumber.push(<Pagination.Item key={i} active={i === page.currentPage} onClick={() => targetButton(i)} >{i}</Pagination.Item>)
        }
        return pageNumber
    }

    const targetButton = (num: number) => {
        dispatch(targetPage(num))
    }

    const firstButton = () => {
        dispatch(firstPage())
    }

    const nextButton = () => {
        dispatch(nextPage())
    }

    const previousButton = () => {
        dispatch(previousPage())
    }

    return (
        <div className="pagination">
            <Pagination size="sm">
                <Pagination.First disabled={page.currentPage === 1} onClick={firstButton} />
                <Pagination.Prev disabled={page.currentPage === 1} onClick={previousButton} />
                {
                    btnNumber()
                }
                <Pagination.Next disabled={page.currentPage === count} onClick={nextButton} />
                <Pagination.Last disabled={page.currentPage === count} onClick={() => targetButton(count)} />
            </Pagination>
        </div>
    )
}

export default Pagin
