import { Component } from "react"
import { Button } from 'react-bootstrap'
import { textBody } from '../Functions/slicerText'

interface Props {
    text: string
}

class HiddenButton extends Component<Props> {

    text_body = textBody(this.props.text)

    constructor(props: any) {
        super(props)
        this.isDisplay()
    }

    isDisplay = () => {
        return this.text_body
    }

    render = () => {
        return <Button>Read more</Button>
    }
}

export default HiddenButton
