import { Component } from 'react'
import Navigation from '../components/Navigation'
import { connect } from 'react-redux'
import { Container, Row, Col, Card, Alert } from 'react-bootstrap'
import HiddenButton from '../components/HiddenButton'
import Api from '../api/Api'
import Pagination from '../components/Pagin'
import { setLoading, setCountBlogs, setIndexPost } from '../store/hooks'


interface Props {
    page: {
        blogs: {
            currentPage: number
            skip: number
        }
        loading: boolean
        countBlogs: number
        index_post: any
    }

    setLoading: any
    setCountBlogs: any
    setIndexPost: any
}


class Index extends Component<Props>{


    setLoading: any
    setCountBlogs: any
    setIndexPost: any

    constructor(props: any) {

        super(props)


        const { setLoading, setCountBlogs, setIndexPost }: any = this.props

        this.setLoading = setLoading
        this.setCountBlogs = setCountBlogs
        this.setIndexPost = setIndexPost

        console.log(this.props)

    }

    componentDidMount = () => {
        console.log(`Mounted`)
        this.updatePage()
    }

    shouldComponentUpdate = (): boolean => {
        // console.log(`Should be updated? --> ${this.data}`)
        return true
    }

    componentDidUpdate = (): void => {
        console.log(`Updated`)
    }

    componentWillUnmount = () => {
        console.log(`Will unmount`)
    }

    updatePage = async (): Promise<void> => {

        try {
            const api = new Api(this.props.page.blogs)
            const data: any = await api.index()

            if (data) {
                this.setLoading()
                this.setIndexPost(data)
                this.setCountBlogs(data[1])
            }


        } catch (error) {
            console.log(`${error}`)
        }
        // this.setState({ data, loading: true })
        // const count = Math.ceil(this.state.data[1] / this.state.params.take)
        // this.setState({ countData: count })
    }

    nextPage = async () => {
        // this.setState({
        //     params: {
        //         currentPage: this.state.params.currentPage + 1,
        //         take: 4
        //     }
        // })
        // const api = new Api(this.state.params)
        // const data = await api.index()
        // this.setState({ data, loading: true })

        // const count = Math.ceil(this.state.data[1] / this.state.params.take)
        // this.setState({ countData: count })
    }

    previousPage = async () => {

        // this.setState({
        //     params: {
        //         currentPage: this.state.params.currentPage - 1,
        //         take: 4
        //     }
        // })

        // const api = new Api(this.state.params)
        // const data = await api.index()
        // this.setState({ data, loading: true })
    }

    targetPage = async (num: number) => {

        // this.setState({
        //     params: {
        //         currentPage: num,
        //         take: 4
        //     }
        // })

        // const api = new Api(this.state.params)
        // const data = await api.index()
        // this.setState({ data, loading: true })

    }

    render = (): JSX.Element => {

        const { index_post, loading } = this.props.page

        return (
            <>
                <Navigation />
                <div className="App">
                    <Container>
                        <Row>
                            <Col md>
                                {loading ?
                                    index_post[0].map((result: any) =>
                                        <div className='App blogs' key={result.uuid}>
                                            <Card>
                                                <Card.Body>
                                                    <Card.Text>
                                                        By : {result.infouser.username}
                                                    </Card.Text>
                                                    <Card.Text>
                                                        Last update : {result.updatedAt}
                                                    </Card.Text>
                                                    <Card.Title>
                                                        {result.title}
                                                    </Card.Title>
                                                    <hr />
                                                    <Card.Text className="content">
                                                        {result.content}
                                                    </Card.Text>
                                                </Card.Body>
                                                {/* <HiddenButton text={result.content} /> */}
                                            </Card>
                                            <br />
                                        </div>
                                    ) : <Alert variant="success">Processing data... </Alert>
                                }
                            </Col>
                        </Row>
                    </Container>
                    {/* <Pagination
                        count={this.state.countData}
                        nextPage={this.nextPage}
                        previousPage={this.previousPage}
                        targetPage={this.targetPage}
                    /> */}
                </div>
            </>
        )
    }
}

const mapStateToProps = (state: any) => ({
    page: state.page
})

const mapDispatchToProps = (dispatch: any) => {
    return {
        setLoading: () => dispatch(setLoading()),
        setCountBlogs: (num: number) => dispatch(setCountBlogs(num)),
        setIndexPost: (data: any) => dispatch(setIndexPost(data))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Index)
