import { FC, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Card, Button, Form } from 'react-bootstrap'
import { setDisplay } from '../store/data'

const Editpost: FC = () => {

    const { edit_post } = useSelector((state: any) => state.data)
    const [getData, setData] = useState({})
    const dispatch = useDispatch()

    const changeValue = (e: any) => {
        const { name } = e.target
        const { value } = e.target
        setData({ ...getData, [name]: value })
    }

    const backTo = () => {
        dispatch(setDisplay(true))
    }

    const update = () => {
        console.log(getData)
    }

    return (
        <div className='App blogs' key={edit_post.uuid} >
            <Card>
                <Card.Body>
                    <Card.Text>
                        By : {edit_post.infouser.username}
                    </Card.Text>
                    <Card.Text>
                        Last update : {edit_post.updatedAt}
                    </Card.Text>
                    <Form.Label>Title</Form.Label>
                    <Form.Control as="textarea" name="title" onChange={changeValue} defaultValue={edit_post.title} rows={1} />
                    <Form.Label>Content</Form.Label>
                    <Form.Control as="textarea" name="content" onChange={changeValue} defaultValue={edit_post.content} rows={5} />
                    <br />
                    <Button variant="dark" onClick={update} >Update</Button>{' '}
                    <Button variant="dark" onClick={backTo} >Cencle</Button>
                </Card.Body>
            </Card>
            <br />
        </div>
    )
}

export default Editpost
