import { FC, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { setMyPost } from '../store/data'
import { Container, Row, Col, Card, Alert } from 'react-bootstrap'
import Navigation from '../components/Navigation'
import Api from '../api/Api'

const Myblogs: FC = () => {

    const dispatch = useDispatch()
    const [loading, setLoading] = useState(false)
    const { user } = useSelector((state: any) => state.data)
    const { uuid } = user



    useEffect(() => {
        const fetch = async (): Promise<void> => {
            setLoading(true)
            const api = new Api({})
            const data = await api.myBlogs({ uuid })
            dispatch(setMyPost(data))
        }
        fetch()
    }, [dispatch, setLoading])

    const { myPost }: any = useSelector((state: any) => state.data)
    // console.log(index_post)

    return (
        <>
            <Navigation />
            <div className="App">
                <Container>
                    <Row>
                        <Col md>
                            {loading ?
                                myPost[0].map((result: any) =>
                                    <div className='App blogs' key={result.uuid}>
                                        <Card>
                                            <Card.Body>
                                                <Card.Text>
                                                    By : {result.infouser.username}
                                                </Card.Text>
                                                <Card.Text>
                                                    Last update : {result.updatedAt}
                                                </Card.Text>
                                                <Card.Title>
                                                    {result.title}
                                                </Card.Title>
                                                <hr />
                                                <Card.Text>
                                                    {result.content}
                                                </Card.Text>
                                            </Card.Body>
                                        </Card>
                                        <br />
                                    </div>
                                ) : <Alert variant="success">Data sedang diproses... </Alert>
                            }
                        </Col>
                    </Row>
                </Container>
            </ div>
        </>
    )
}

export default Myblogs
