import { Component } from "react"
import { Pagination } from 'react-bootstrap'
import { connect } from 'react-redux'
import { firstPage, nextPage, previousPage, targetPage } from '../store/hooks'
import Api from '../api/Api'


interface Props {
    page: {
        blogs: {
            currentPage: number
            take: number
        }
        countBlogs: number
        ceil: number
    }

    firstPage: any
    nextPage: any
    previousPage: any
    targetPage: any

}



class Paginate extends Component<Props> {


    btnNumber = () => {

        const pageNumber = []
        for (let i = 1; i <= Math.ceil(this.props.page.countBlogs / this.props.page.blogs.take); i++) {
            pageNumber.push(<Pagination.Item key={i} active={i === this.props.page.blogs.currentPage} onClick={() => this.props.targetPage(i)} >{i}</Pagination.Item>)
        }
        return pageNumber
    }

    targetButton = (num: number) => {
        this.props.targetPage(num)
    }

    firstButton = () => {
        this.props.targetPage(1)
    }

    nextButton = () => {
        const next = this.props.nextPage()
        this.updateData()
    }

    previousButton = () => {
        this.props.previousPage()
        let { currentPage, take } = this.props.page.blogs
        currentPage--
        console.log({ currentPage, take })
    }

    lastButton = () => {
        this.props.targetPage(Math.ceil(this.props.page.countBlogs / this.props.page.blogs.take))
    }

    updateData = async (): Promise<any> => {
        let { currentPage, take } = this.props.page.blogs
        currentPage++
        console.log({ currentPage, take })
    }

    render = () => {

        return (
            <div className="pagination">
                <Pagination size="sm">
                    <Pagination.First onClick={this.firstButton} disabled={this.props.page.blogs.currentPage === 1} />
                    <Pagination.Prev onClick={this.previousButton} disabled={this.props.page.blogs.currentPage === 1} />
                    {
                        this.btnNumber()
                    }
                    <Pagination.Next onClick={this.nextButton} disabled={this.props.page.blogs.currentPage === this.props.page.ceil} />
                    <Pagination.Last onClick={this.lastButton} disabled={this.props.page.blogs.currentPage === this.props.page.ceil} />
                </Pagination>
            </div>
        )
    }
}

const mapStateToProps = (state: any) => ({
    page: state.page
})

const mapDispatchToProps = (dispatch: any) => ({
    firstPage: () => dispatch(firstPage()),
    nextPage: () => dispatch(nextPage()),
    previousPage: () => dispatch(previousPage()),
    targetPage: (num: number) => dispatch(targetPage(num))
})

export default connect(mapStateToProps, mapDispatchToProps)(Paginate)
