import { FC } from 'react'
import { Navbar, Nav, Container } from 'react-bootstrap'

const Navigation: FC = () => {

    const path: string = window.location.pathname


    const isHome = (path: string) => {

        const homeTo = () => {
            return (
                <>
                    <Nav.Link href="/home/myblogs" active={path === '/home/myblogs'} >My blogs</Nav.Link>
                    <Nav.Link href="/home/info" active={path === '/home/info'} >Info</Nav.Link>
                    <Navbar.Toggle />
                    <Navbar.Collapse className="justify-content-end">
                        <Navbar.Text>
                            <div className="logout">
                                <a href="/">Logout</a>
                            </div>
                        </Navbar.Text>
                    </Navbar.Collapse>
                </>
            )
        }

        switch (path) {
            case '/home':
                return homeTo()

            case '/home/myblogs':
                return homeTo()

            case '/home/info':
                return homeTo()

            default:
                return (
                    <>
                        <Nav.Link href="/login" active={path === '/login'} >Login</Nav.Link>
                        <Nav.Link href="/signup" active={path === '/signup'} >Signup</Nav.Link>
                    </>
                )

        }
    }

    return (
        <div className="navigation">
            <Navbar bg="dark" variant="dark">
                <Container>
                    <Navbar.Brand href="/">SimpleBlog</Navbar.Brand>
                    <Nav className="me-auto">
                        {isHome(path)}
                    </Nav>
                </Container>
            </Navbar>
        </ div>
    )

}

export default Navigation
