import { Component } from 'react'
import { connect } from 'react-redux'
import { nextButtonNumber, reset } from '../store/hooks'
import { Button } from 'react-bootstrap'

interface Props {
    page: {
        button_number: number
    }

    nextButtonNumber: any
    reset: any
}

class Try extends Component<Props>{

    nextButtonNumber = this.props.nextButtonNumber
    reset = this.props.reset

    constructor(props: any) {
        super(props)

        console.log(this.props)
    }

    // shouldComponentUpdate = (): boolean => {
    //     return this.props.page.button_number < 10 ? true : false
    // }

    render = () => {

        return (
            <div className="App">
                <h1>Number : {this.props.page.button_number}</h1>
                <Button onClick={() => this.nextButtonNumber()}>Add</Button> {' '}
                <Button onClick={() => this.reset()}>Reset</Button>
            </div>
        )
    }
}

const mapStateToProps = (state: any) => ({
    page: state.page
})

const mapDispatchToProps = (dispatch: any) => {
    return {
        nextButtonNumber: () => dispatch(nextButtonNumber()),
        reset: () => dispatch(reset())
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Try)
