import { Component } from "react"
import { Card, Form, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'

interface State {
    user: any
}

class Login extends Component<any, State> {


    constructor(props: any) {
        super(props)
        this.state = {
            user: {
                username: '' as string,
                password: '' as string
            }
        }
    }

    getChange = (e: any) => {
        const { name, value } = e.target
        this.setState({
            user: { ...this.state.user, [name]: value }
        })
    }

    submit = () => {
        console.log(this.state.user)
    }

    render = () => {
        return (
            <>
                <Navigation />
                <div className="App">
                    <Card>
                        <h3>Login</h3>
                        <br />
                        <Form.Group>
                            <Form.Control type="text" name="username" onChange={this.getChange} placeholder="Username" />
                        </Form.Group>
                        <br />
                        <Form.Group>
                            <Form.Control type="password" name="password" onChange={this.getChange} placeholder="Password" />
                        </Form.Group>
                        <br />
                        <Button onClick={this.submit}>Login</Button>
                    </Card>
                </div>
            </>
        )
    }

}

export default Login
