import { Component } from "react"
import { Button, Form, Card } from 'react-bootstrap'
import { connect } from 'react-redux'
import Api from '../api/Api'
import { setHiddenComments, setIsHide } from '../store/hooks'


interface Props {
    data: {
        login: boolean
        user: {
            uuid: string
            username: string
        }
        index_post: {
            0: any
        }
        isHide: string
    }

    page: { blogs: any }

    result: any
    infocomments: any
    setHiddenComments: any
    setIsHide: any
    history?: any
}

interface State {
    comments: string
    isHide: string
    dataHide: any
    pushData: any
}


class Comment extends Component<Props | any, State> {


    constructor(props: any) {
        super(props)
        this.state = {
            comments: '',
            isHide: this.props.data.isHide,
            dataHide: [],
            pushData: { kuda: 'jaran' }
        }
    }


    static getDerivedStateFromProps = (props: any, state: any) => {
        state.dataHide = props.infocomments
        if (state.pushData) {
            console.log(state.pushData)
        }
        return null
    }


    componentDidMount = () => {
        // console.log(this.state.dataHide)
    }


    commentValue = (e: any) => {
        const { value } = e.target
        this.setState({ comments: value })
    }

    submit = async (e: any): Promise<void> => {

        const { comments } = this.state

        if (this.props.data.login === false) {
            alert('You need to login!')
        } else if (comments === '') {
            alert(`Don't submit empty data!`)
        } else {
            // console.log(this.state)
            const { uuid } = this.props.result
            const data = {
                username: this.props.data.user.username,
                user_uuid: this.props.data.user.uuid,
                post_uuid: uuid,
                comments
            }

            try {

                const result = { uuid: "ccb20589-d3b8-4ddb-af8e-3546de137331", createdAt: "2021-08-20T22:38:39.090Z", updatedAt: "2021-08-20T22:38:39.090Z", username: "Pusing", comments: "Aing!" }

                const { uuid, createdAt, updatedAt, username, comments } = result

                this.setState({
                    dataHide: { uuid, createdAt, updatedAt, username, comments }
                }, () => {
                    const count = this.state.dataHide.length
                    const obj = this.state.dataHide
                    obj[count + 1] = result
                    console.log(obj)
                })


                // const api = new Api(data)
                // const result: any = await api.addComment()

                // if (result) {
                //     const { uuid, createdAt, updatedAt, username, comments } = result
                //     this.setState({ dataHide: { ...this.state.dataHide, uuid, createdAt, updatedAt, username, comments } }, () => {
                //         console.log(this.state.dataHide)
                //     })
                // }

            } catch (error) {
                console.log(`${error}`)
            }

        }
    }

    displayComments = (e: any) => {
        return this.state.isHide === 'none' ?
            this.setState({ isHide: 'block' }) :
            this.setState({ isHide: 'none' })
    }


    render = () => {

        return (
            <>
                <Card.Text className="displayComments" onClick={this.displayComments}>{this.props.infocomments.length} comments</Card.Text>
                <hr />
                {
                    this.state.dataHide.length !== 0 ?
                        <Card className="cardComments" style={{ display: this.state.isHide }}>
                            {
                                this.state.dataHide.map((res: any) =>
                                    <div key={res.uuid}>
                                        <p>{res.username} :</p>
                                        <p>{res.comments}</p>
                                        <hr />
                                    </ div>
                                )
                            }
                        </ Card> :
                        null
                }
                <br />
                <Form.Control as="textarea" placeholder="Add comments" name="comments" onChange={this.commentValue} rows={2} />
                <br />
                <Button variant="dark" onClick={this.submit}>Comment</Button>
            </>
        )
    }
}


const mapStateToProps = (state: any) => ({
    data: state.data,
    page: state.page
})

const mapDispatchToProps = (dispatch: any) => ({
    setHiddenComments: (data: any) => dispatch(setHiddenComments(data)),
    setIsHide: (data: string) => dispatch(setIsHide(data))
})


export default connect(mapStateToProps, mapDispatchToProps)(Comment)
