import { Component } from "react"
import { Button, Form } from 'react-bootstrap'
import { connect } from 'react-redux'


interface Props {
    data: {
        login: boolean
    }
    info: any
}

interface State {
    comment: string
}


class Comment extends Component<Props, State> {

    constructor(props: any) {
        super(props)
        this.state = {
            comment: ''
        }
    }

    commentValue = (e: any) => {
        const { value } = e.target
        this.setState({ comment: value })
    }

    submit = async (): Promise<void> => {
        if (this.props.data.login === false) {
            alert('Login first!')
        } else {
            // console.log(this.state)
            const { comment } = this.state
            const { uuid, infouser } = this.props.info
            const data = {
                user_uuid: infouser.uuid,
                post_uuid: uuid,
                comment
            }
            console.log(data)
        }
    }

    render = () => {
        return (
            <>
                <br /> <hr />
                <Form.Control as="textarea" placeholder="Add comments" name="comment" onChange={this.commentValue} rows={2} />
                <br />
                <Button variant="dark" onClick={this.submit}>Comment</Button>
            </>
        )
    }
}


const mapStateToProps = (state: any) => ({
    data: state.data
})


export default connect(mapStateToProps)(Comment)
