import { Component } from "react"
import { Pagination } from 'react-bootstrap'
import { connect } from 'react-redux'
import { firstPage, nextPage, previousPage, targetPage } from '../store/hooks'


interface Props {
    page: {
        blogs: {
            currentPage: number
            take: number
        }
        countBlogs: number
    }

    firstPage: any
    nextPage: any
    previousPage: any
    targetPage: any
}


class Paginate extends Component<Props> {


    count: number = Math.ceil(this.props.page.countBlogs / this.props.page.blogs.take)
    currentPage: number = this.props.page.blogs.currentPage

    constructor(props: any) {
        super(props)
        console.log(this.currentPage)
    }

    btnNumber = () => {

        const pageNumber = []
        for (let i = 1; i <= this.count; i++) {
            pageNumber.push(<Pagination.Item key={i} active={i === this.currentPage} onClick={() => this.props.targetPage(i)} >{i}</Pagination.Item>)
        }
        return pageNumber
    }

    targetButton = (num: number) => {
        this.props.targetPage(num)
    }

    firstButton = () => {
        this.props.targetPage(1)
    }

    nextButton = () => {
        this.props.nextPage()
    }

    previousButton = () => {
        this.props.previousPage()
    }

    lastButton = () => {
        this.props.targetPage(this.count)
    }

    render = () => {

        return (
            <div className="pagination">
                <Pagination size="sm">
                    <Pagination.First disabled={this.currentPage === 1} />
                    <Pagination.Prev disabled={this.currentPage === 1} />
                    {
                        this.btnNumber()
                    }
                    <Pagination.Next /* disabled={currentPage === this.props.count} */ />
                    <Pagination.Last /* disabled={currentPage === this.props.count} */ />
                </Pagination>
            </div>
        )
    }
}

const mapStateToProps = (state: any) => ({
    page: state.page
})

const mapDispatchToProps = (dispatch: any) => ({
    firstPage: () => dispatch(firstPage()),
    nextPage: () => dispatch(nextPage()),
    previousPage: () => dispatch(previousPage()),
    targetPage: (num: number) => dispatch(targetPage(num))
})

export default connect(mapStateToProps, mapDispatchToProps)(Paginate)
