import { FC } from 'react'
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap'
import { useSelector } from 'react-redux'
import Logout from './Logout'

const Navigation: FC = () => {

    const path: string = window.location.pathname
    const { user } = useSelector((state: any) => state.data)

    const isHome = (path: string) => {

        const currentNav = () => {
            return (
                <>
                    <Nav.Link href="/home" active={path === '/home'} >Home</Nav.Link>
                    <Nav.Link href="/home/myblogs" active={path === '/home/myblogs'} >My blogs</Nav.Link>
                    <Nav.Link href="/home/info" active={path === '/home/info'} >Info</Nav.Link>

                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="me-auto">
                            <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
                                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item >
                                    <Logout />
                                </NavDropdown.Item>
                            </NavDropdown>
                        </Nav>
                    </Navbar.Collapse>
                </>
            )
        }

        switch (path) {
            case '/home':
            case '/home/info':
            case '/home/myblogs':
                return currentNav()

            case "/":
                const key = Object.keys(user)
                if (key.length === 0) {
                    return (
                        <>
                            <Nav.Link href="/login" >Login</Nav.Link>
                            <Nav.Link href="/signup" >Signup</Nav.Link>
                        </>
                    )
                } else return currentNav()

            default:
                return (
                    <>
                        <Nav.Link href="/login" active={path === '/login'} >Login</Nav.Link>
                        <Nav.Link href="/signup" active={path === '/signup'} >Signup</Nav.Link>
                    </>
                )

        }
    }

    return (
        <div className="navigation">
            <Navbar bg="dark" variant="dark">
                <Container>
                    <Navbar.Brand href="/">SimpleBlog</Navbar.Brand>
                    <Nav className="me-auto">
                        {isHome(path)}
                    </Nav>
                </Container>
            </Navbar>
        </ div>
    )

}

export default Navigation
