import { Component } from "react"
import { Button } from 'react-bootstrap'


const slicer = (text: string) => {
    const slicer = text.slice(0, 200)
    return `${slicer}...`
}

export const textBody = (text: any) => {

    const words = text.match(/(\w+)/g).length
    if (words >= 100) {
        // console.log(words)
    }
    return words <= 100 ? text : slicer(text)
}


interface Props {
    text: string
}

interface State {
    textBody: string
}

class HiddenButton extends Component<Props, State> {

    id: number = 0

    constructor(props: Props) {
        super(props)
        this.state = {
            textBody: this.props.text
        }
    }


    readMore = () => {
        console.log(this.state.textBody)
    }

    isDisplay = () => {
        this.id++
        const { text }: any = this.props
        const words = text.match(/(\w+)/g).length
        return words >= 100 ? <Button onClick={this.readMore} id={`${this.id}`} >Read more</Button> : <p></p>
    }

    render = () => {
        return this.isDisplay()
    }
}

export default HiddenButton
